export default function MapView({ start, selectedPlaces, routes }) {
  return (
    <div style={{ display: "grid", gap: 10 }}>
      <h2 style={{ margin: 0 }}>지도</h2>

      <div
        style={{
          height: 420,
          borderRadius: 10,
          border: "1px dashed #aaa",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: 16,
        }}
      >
        <div style={{ textAlign: "center", color: "#666" }}>
          <div style={{ fontWeight: 700, marginBottom: 8 }}>MapView 자리</div>
          <div>출발지: {start ? JSON.stringify(start) : "미설정"}</div>
          <div>관광지 수: {selectedPlaces.length}</div>
          <div style={{ marginTop: 8 }}>
            추천경로: {routes?.recommended ? "있음" : "없음"} / 최단경로:{" "}
            {routes?.shortest ? "있음" : "없음"}
          </div>
          <div style={{ marginTop: 8, fontSize: 12 }}>
            (다음 단계에서 카카오맵/네이버맵/Leaflet 등 실제 지도 붙이자)
          </div>
        </div>
      </div>
    </div>
  );
}
