import { useState } from "react";
import { Link } from "react-router-dom";
import PlannerForm from "../components/planner/PlannerForm";
import MapView from "../components/planner/MapView";
import ComparisonPanel from "../components/planner/ComparisonPanel";

export default function PlannerPage() {
  const [startMode, setStartMode] = useState("current"); // current | custom
  const [startInput, setStartInput] = useState("");
  const [selectedPlaces, setSelectedPlaces] = useState([]);
  const [weights, setWeights] = useState({
    scenery: 5,
    crowd: 5,
    cost: 5,
  });

  const [routes, setRoutes] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const start =
    startMode === "current"
      ? { mode: "current" }
      : { mode: "custom", value: startInput };

  const onGenerate = async () => {
    setIsLoading(true);

    // 지금은 mock 결과로 UI 흐름만 확인
    // 나중에 routesApi.getRoutes(input)로 바꾸면 됨
    await new Promise((r) => setTimeout(r, 600));

    setRoutes({
      recommended: {
        distanceKm: 12.4,
        durationMin: 46,
        score: 83,
        polyline: [
          [37.5665, 126.978],
          [37.57, 126.985],
        ],
        stops: selectedPlaces,
      },
      shortest: {
        distanceKm: 10.6,
        durationMin: 38,
        score: 70,
        polyline: [
          [37.5665, 126.978],
          [37.569, 126.982],
        ],
        stops: selectedPlaces,
      },
    });

    setIsLoading(false);
  };

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h1 style={{ margin: 0 }}>경로 만들기</h1>
        <Link to="/">
          <button>홈으로</button>
        </Link>
      </div>

      <div
        style={{
          marginTop: 16,
          display: "grid",
          gridTemplateColumns: "2fr 1fr",
          gap: 16,
          alignItems: "start",
        }}
      >
        {/* 왼쪽 */}
        <div style={{ display: "grid", gap: 16 }}>
          <div style={{ border: "1px solid #ddd", borderRadius: 10, padding: 16 }}>
            <PlannerForm
              startMode={startMode}
              setStartMode={setStartMode}
              startInput={startInput}
              setStartInput={setStartInput}
              selectedPlaces={selectedPlaces}
              setSelectedPlaces={setSelectedPlaces}
              weights={weights}
              setWeights={setWeights}
              onGenerate={onGenerate}
              isLoading={isLoading}
            />
          </div>

          <MapView start={start} selectedPlaces={selectedPlaces} routes={routes} />
        </div>

        {/* 오른쪽 */}
        <ComparisonPanel routes={routes} />
      </div>
    </div>
  );
}
