function Metric({ label, a, b, unit }) {
  const delta = a != null && b != null ? a - b : null;
  const deltaText =
    delta == null ? "-" : `${delta > 0 ? "+" : ""}${delta.toFixed(1)}${unit}`;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1fr auto",
        gap: 8,
        padding: "8px 0",
        borderBottom: "1px solid #eee",
      }}
    >
      <div style={{ fontWeight: 600 }}>{label}</div>
      <div style={{ textAlign: "right" }}>
        <div style={{ fontSize: 13, color: "#444" }}>
          추천 {a ?? "-"}
          {a != null ? unit : ""} / 최단 {b ?? "-"}
          {b != null ? unit : ""}
        </div>
        <div style={{ fontSize: 12, color: "#777" }}>차이 {deltaText}</div>
      </div>
    </div>
  );
}

export default function ComparisonPanel({ routes }) {
  const rec = routes?.recommended;
  const sh = routes?.shortest;

  return (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: 10,
        padding: 16,
        position: "sticky",
        top: 16,
      }}
    >
      <h2 style={{ marginTop: 0 }}>추천 vs 최단 비교</h2>

      {!routes && (
        <div style={{ color: "#666", fontSize: 14 }}>
          경로를 생성하면 비교 결과가 표시돼.
        </div>
      )}

      {routes && (
        <div style={{ display: "grid", gap: 4 }}>
          <Metric
            label="거리"
            a={rec?.distanceKm}
            b={sh?.distanceKm}
            unit="km"
          />
          <Metric
            label="시간"
            a={rec?.durationMin}
            b={sh?.durationMin}
            unit="min"
          />
          <Metric label="점수" a={rec?.score} b={sh?.score} unit="" />

          <div style={{ marginTop: 12, fontSize: 12, color: "#666" }}>
            * 점수/거리/시간의 의미와 계산 방식은 나중에 FastAPI 결과 스키마에 맞춰
            조정하면 돼.
          </div>
        </div>
      )}
    </div>
  );
}
