const OLAT = 38.0;     // Í∏∞Ï§Ä ÏúÑÎèÑ
const XO = 43;         // ÏõêÏ†ê X Ï¢åÌëú
const YO = 136;        // ÏõêÏ†ê Y Ï¢åÌëú

function convertToGrid(lat, lon) {
    const DEGRAD = Math.PI / 180.0;
    //const RADDEG = 180.0 / Math.PI;

    const re = RE / GRID;
    const slat1 = SLAT1 * DEGRAD;
    const slat2 = SLAT2 * DEGRAD;
    const olon = OLON * DEGRAD;
    const olat = OLAT * DEGRAD;

    let sn = Math.tan(Math.PI * 0.25 + slat2 * 0.5) / Math.tan(Math.PI * 0.25 + slat1 * 0.5);
    sn = Math.log(Math.cos(slat1) / Math.cos(slat2)) / Math.log(sn);
    let sf = Math.tan(Math.PI * 0.25 + slat1 * 0.5);
    sf = Math.pow(sf, sn) * Math.cos(slat1) / sn;
    let ro = Math.tan(Math.PI * 0.25 + olat * 0.5);
    ro = re * sf / Math.pow(ro, sn);

    // ÏÇ¨Ïö©ÏûêÏùò ÏúÑÎèÑ/Í≤ΩÎèÑ Î≥ÄÌôò
    let ra = Math.tan(Math.PI * 0.25 + lat * DEGRAD * 0.5);
    ra = re * sf / Math.pow(ra, sn);
    let theta = lon * DEGRAD - olon;
    if (theta > Math.PI) theta -= 2.0 * Math.PI;
    if (theta < -Math.PI) theta += 2.0 * Math.PI;
    theta *= sn;

    // ÏµúÏ¢Ö Í≤©Ïûê Ï¢åÌëú (nx, ny) Í≥ÑÏÇ∞
    const nx = Math.floor(ra * Math.sin(theta) + XO + 0.5);
    const ny = Math.floor(ro - ra * Math.cos(theta) + YO + 0.5);

    return { nx, ny };
}

/**
 * Í∏∞ÏÉÅÏ≤≠ Îã®Í∏∞ÏòàÎ≥¥ API Ìò∏Ï∂úÏùÑ ÏúÑÌïú base_dateÏôÄ base_timeÏùÑ Í≥ÑÏÇ∞Ìï©ÎãàÎã§.
 * APIÎäî 3ÏãúÍ∞Ñ Îã®ÏúÑÎ°ú Î∞úÌëúÎê©ÎãàÎã§. (02, 05, 08, 11, 14, 17, 20, 23Ïãú Ï†ïÍ∞Å)
 */
function getBaseDateTime() {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');

    let hour = now.getHours();
    let base_time = '';

    // Î∞úÌëú ÏãúÍ∞ÑÏùÑ Í∏∞Ï§ÄÏúºÎ°ú Í∞ÄÏû• ÏµúÍ∑ºÏùò ÏãúÍ∞ÑÏùÑ Ï∞æÏäµÎãàÎã§.
    if (hour >= 23) base_time = '2300';
    else if (hour >= 20) base_time = '2000';
    else if (hour >= 17) base_time = '1700';
    else if (hour >= 14) base_time = '1400';
    else if (hour >= 11) base_time = '1100';
    else if (hour >= 8) base_time = '0800';
    else if (hour >= 5) base_time = '0500';
    else if (hour >= 2) base_time = '0200';
    else base_time = '2300'; // 00Ïãú, 01ÏãúÏùò Í≤ΩÏö∞ Ï†ÑÎÇ† 23Ïãú Î∞úÌëú Í∏∞Ï§Ä

    // ÌòÑÏû¨ ÏãúÍ∞ÅÏù¥ Î∞úÌëú ÏãúÍ∞Ñ Í∏∞Ï§ÄÎ≥¥Îã§ Îπ†Î•¥Îã§Î©¥, base_dateÎ•º Ï†ÑÎÇ†Î°ú ÏÑ§Ï†ïÌï©ÎãàÎã§.
    let base_date = `${year}${month}${day}`;
    if (base_time === '2300' && now.getHours() < 2) {
        const yesterday = new Date(now);
        yesterday.setDate(now.getDate() - 1);
        base_date = `${yesterday.getFullYear()}${String(yesterday.getMonth() + 1).padStart(2, '0')}${String(yesterday.getDate()).padStart(2, '0')}`;
    }

    return { base_date, base_time };
}

// Í≤©Ïûê Îç∞Ïù¥ÌÑ∞(item) Î∞∞Ïó¥ÏùÑ ÏãúÍ∞ÑÎ≥Ñ ÏòàÎ≥¥ Í∞ùÏ≤¥Î°ú Î≥ÄÌôòÌïòÎäî Ìï®Ïàò
function processForecastData(items) {
    const forecastMap = new Map();

    items.forEach(item => {
        const key = `${item.fcstDate}_${item.fcstTime}`;
        if (!forecastMap.has(key)) {
            forecastMap.set(key, {
                baseDate: item.baseDate,
                baseTime: item.baseTime,
                fcstDate: item.fcstDate,
                fcstTime: item.fcstTime
            });
        }

        // category (Ïπ¥ÌÖåÍ≥†Î¶¨ ÏΩîÎìú)Ïóê Îî∞Îùº Í∞íÏùÑ Ìï†Îãπ
        forecastMap.get(key)[item.category] = item.fcstValue;
    });

    return Array.from(forecastMap.values());
}

// ÌïòÎäò ÏÉÅÌÉú(SKY) ÏΩîÎìú Î≥ÄÌôò
function getSkyDescription(code) {
    if (!code) return 'Ï†ïÎ≥¥ ÏóÜÏùå';
    switch (String(code)) {
        case '1': return 'ÎßëÏùå';
        case '3': return 'Íµ¨Î¶Ñ ÎßéÏùå';
        case '4': return 'ÌùêÎ¶º';
        default: return `ÏΩîÎìú ${code}`;
    }
}
// ÌíçÌñ•(VEC) ÏΩîÎìú Î≥ÄÌôò
function getWindDirection(deg) {
    if (deg === undefined || deg === null) return 'Ï†ïÎ≥¥ ÏóÜÏùå';

    // Ïà´ÏûêÎ•º Ï†ïÏàòÎ°ú Î≥ÄÌôò ÌõÑ 22.5ÎèÑÎ•º ÎçîÌï¥ Î∞òÏò¨Î¶º Ï≤òÎ¶¨
    const val = parseInt(deg) + 22.5;
    const directions = [
        "Î∂Å", "Î∂ÅÎ∂ÅÎèô", "Î∂ÅÎèô", "ÎèôÎ∂ÅÎèô", "Îèô", "ÎèôÎÇ®Îèô", "ÎÇ®Îèô", "ÎÇ®ÎÇ®Îèô",
        "ÎÇ®", "ÎÇ®ÎÇ®ÏÑú", "ÎÇ®ÏÑú", "ÏÑúÎÇ®ÏÑú", "ÏÑú", "ÏÑúÎ∂ÅÏÑú", "Î∂ÅÏÑú", "Î∂ÅÎ∂ÅÏÑú", "Î∂Å"
    ];

    // 360ÎèÑÎ•º 16Î∞©ÏúÑÎ°ú ÎÇòÎàà Í∞íÏúºÎ°ú Ïù∏Îç±Ïä§Î•º Í≥ÑÏÇ∞
    const index = Math.floor(val / 45 * 2 + 0.5) % 16;

    return directions[index];
}

// Í∞ïÏàò ÌòïÌÉú(PTY) ÏΩîÎìú Î≥ÄÌôò
function getPtyDescription(code) {
    if (!code) return 'Í∞ïÏàò ÏóÜÏùå';
    switch (String(code)) {
        case '0': return 'Í∞ïÏàò ÏóÜÏùå';
        case '1': return 'ÎπÑ';
        case '2': return 'ÎπÑ/Îàà';
        case '3': return 'Îàà';
        case '4': return 'ÏÜåÎÇòÍ∏∞';
        case '5': return 'ÎπóÎ∞©Ïö∏';
        case '6': return 'ÎπóÎ∞©Ïö∏/ÎààÎÇ†Î¶º';
        case '7': return 'ÎààÎÇ†Î¶º';
        default: return `ÏΩîÎìú ${code}`;
    }
}


function ShortTermWeather() {
    // NX, NYÎ•º ÎèôÏ†ÅÏúºÎ°ú Ï†ÄÏû•ÌïòÍ∏∞ ÏúÑÌïú ÏÉàÎ°úÏö¥ ÏÉÅÌÉú Ï∂îÍ∞Ä
    const [coords, setCoords] = useState({ nx: null, ny: null });
