        } else {
            setError("Ïù¥ Î∏åÎùºÏö∞Ï†ÄÎäî ÏúÑÏπò Ï†ïÎ≥¥(Geolocation)Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
            setLoading(false);
        }
    }, []); // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú Ìïú Î≤àÎßå ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏöîÏ≤≠Ìï©ÎãàÎã§.

    // ----------------------------------------------------
    // 3. ÎÇ†Ïî® Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Î°úÏßÅ (ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÌôïÎ≥¥Îêú ÌõÑÏóêÎßå Ïã§Ìñâ)
    // --- [Hook 2] Ï¢åÌëú(coords)Í∞Ä ÏÑ§Ï†ïÎêú ÌõÑ API Ìò∏Ï∂ú ---
    useEffect(() => {
        // Ï¢åÌëúÍ∞Ä ÏïÑÏßÅ ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÍ±∞ÎÇò ÏóêÎü¨Í∞Ä ÏûàÎäî Í≤ΩÏö∞ API Ìò∏Ï∂úÏùÑ ÌïòÏßÄ ÏïäÏäµÎãàÎã§.
        if (coords.nx === null || coords.ny === null || error) {
            return;
        }

        const fetchWeather = async () => {
            setLoading(true);
            const { base_date, base_time } = getBaseDateTime();

            // ‚úÖ ÏàòÏ†ïÎêú Î∂ÄÎ∂Ñ: coords.nx, coords.nyÎ•º ÏÇ¨Ïö©
