    if (weatherList.length === 0) {
        return <div style={{ textAlign: 'center', padding: '20px' }}>ÎÇ†Ïî® Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. (Í∏∞Ï§Ä ÎÇ†Ïßú: {getBaseDateTime().base_date}, ÏãúÍ∞Ñ: {getBaseDateTime().base_time})</div>;
    }

    return (
